(function () {
    cmpCore.registryComponent({
        name: "CSpecialization",
        templateId: "CSpecializationTemplate",
        methods: {

        },
        afterRender: function () {
            var self = this;

            this.containerElm.addEventListener("click", function (evt) {
                var target = evt.target;

                target = target.closest(".open_course");

                evt.preventDefault();
                evt.stopPropagation();

                if (!target) {
                    return;
                }

                var courseId = target.getAttribute("data-course")
                console.log("click course");

                utils.routing.route("/specialization/" + courseId);


            });
        }
    });
})();

